import { Component, OnInit } from '@angular/core';
import { Select, Store } from '@ngxs/store';
import { BehaviorSubject, interval, ReplaySubject, Subject, take, scan, Observable, map} from 'rxjs';
import { AddValue, RemoveValue } from './store/test.actions';
import { StateModel } from './store/test.model';
import { TestState } from './store/test.state';

const inititalValue: string = 'BehaviorSubject init';
const bufferSize: number = 3;

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.less']
})

export class AppComponent implements OnInit {
  constructor(private store: Store){}

  @Select(TestState) storeData$: Observable<StateModel>;

  subject$: Subject<string> = new Subject();
  replySubject$: ReplaySubject<string> = new ReplaySubject(bufferSize);
  behaviorSubject$: BehaviorSubject<string> = new BehaviorSubject(inititalValue);

  stream$ = interval(1000).pipe(
    take(3),
    scan((acc, value) =>  acc + value, 0)
  );

  storeValues$: Observable<string[]>;

  ngOnInit(): void {
    this.storeValues$ = this.storeData$.pipe(map(item => item.values));
  }

  onSubjectClick(): void {
    this.subject$.next('subject event');
  }

  onReplySubject(): void {
    this.replySubject$.next('replySubject event');
  }

  onBehaviorSubject(): void {
    this.behaviorSubject$.next('behaviorSubject event');
  }

  addStoreValue(value: string) {
    this.store.dispatch(new AddValue(value));
  }

  removeStoreValue(value: string) {
    this.store.dispatch(new RemoveValue(value));
  }
}

<h1> Test value : {{stream$ | async }}</h1>
<h2> Test state: </h2>
<ul>
  <li *ngFor="let item of (storeValues$ | async)"> {{item}} </li>
</ul>

<div class="wrapper">
  <section>
    <button (click)="onSubjectClick()"> onSubjectClick </button>
    <h2>{{subject$ | async}}</h2>
  </section>
  <section>
    <button (click)="onReplySubject()"> onReplySubject </button>
    <h2>{{replySubject$ | async}}</h2>
  </section>
  <section>
    <button (click)="onBehaviorSubject()"> onBehaviorSubject </button>
    <h2>{{behaviorSubject$ | async}}</h2>
  </section>
</div>

<h2 class="header"> {{'Content' | custom | uppercase}} </h2>

<section class="wrapper">
  <input type="text" placeholder="value to add"  (keydown.enter)="addStoreValue(input1.value)" #input1>
</section>
<section class="wrapper">
  <input type="text" placeholder="value to remove" (keydown.enter)="removeStoreValue(input2.value)" #input2>
</section>

<nav>
  <ul>
    <li><a routerLink="/child" ariaCurrentWhenActive="page">First Component</a></li>
    <li><a routerLink="/second-child" ariaCurrentWhenActive="page">Second Component</a></li>
  </ul>
</nav>

<div class="routing-wrapper">
  <router-outlet></router-outlet>
</div>
